import React, { useState } from "react";
import { BrowserRouter as Router, Routes, Route, Link } from "react-router-dom";
import HomePage from "./components/HomePage";
import ProductDetails from "./components/ProductDetails";
import Cart from "./components/Cart";

import homeIcon from "./assets/home.png";
import cartIcon from "./assets/cart.png";
import heartIcon from "./assets/heart.png"; // Ensure this path is correct

import "./App.scss";

function App() {
  const [cart, setCart] = useState([]);

  const addToCart = (product) => {
    const exists = cart.find((item) => item.id === product.id);
    if (exists) {
      setCart(
        cart.map((item) =>
          item.id === product.id
            ? { ...item, quantity: item.quantity + 1 }
            : item
        )
      );
    } else {
      setCart([...cart, { ...product, quantity: 1 }]);
    }
  };

  const removeFromCart = (productId) => {
    setCart(cart.filter((item) => item.id !== productId));
  };

  return (
    <Router>
      <div>
        <nav className="navbar navbar-expand-lg navbar-light bg-light">
          <div className="container-fluid justify-content-between">
            {/* Left-aligned items */}
            <Link to="/" className="nav-link">
              <img src={homeIcon} alt="Home" className="nav-icon" />
            </Link>

            {/* Center-aligned brand/logo */}
            <Link
              to="/"
              className="navbar-brand mx-auto d-flex align-items-center"
            >
              All <img src={heartIcon} alt="Love" width="30" className="mx-2" />{" "}
              Mart
            </Link>

            {/* Right-aligned cart icon */}
            <Link to="/cart" className="nav-link position-absolute end-0">
              <img src={cartIcon} alt="Cart" className="nav-icon" />
            </Link>
          </div>
        </nav>
        <Routes>
          <Route path="/" element={<HomePage addToCart={addToCart} />} />
          <Route path="/product/:productId" element={<ProductDetails />} />
          <Route
            path="/cart"
            element={<Cart cart={cart} setCart={setCart} />}
          />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
