import React, { useState, useEffect } from "react";
import axios from "axios";
import "../HomePage.css";

function HomePage({ addToCart }) {
  const [products, setProducts] = useState([]);
  const [categories, setCategories] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState("");
  const [showCategories, setShowCategories] = useState(false);
  const [currentPage, setCurrentPage] = useState(1);
  const [totalPages, setTotalPages] = useState(1);
  const [searchQuery, setSearchQuery] = useState("");
  const [selectedProduct, setSelectedProduct] = useState(null); // State to track the selected product for modal view

  useEffect(() => {
    console.log(
      "Fetching products for page:",
      currentPage,
      "and category:",
      selectedCategory
    );
    fetchProducts();
  }, [currentPage, selectedCategory]);

  useEffect(() => {
    console.log("Fetching categories...");
    fetchCategories();
  }, []);

  const fetchProducts = async () => {
    let url = "https://dummyjson.com/products";
    let params = new URLSearchParams({
      skip: (currentPage - 1) * 10,
      limit: 10,
    });

    if (selectedCategory) {
      url += `/category/${selectedCategory}?${params.toString()}`;
    } else {
      url += `?${params.toString()}`;
    }

    try {
      const response = await axios.get(url);
      setProducts(response.data.products || []);
      const totalProducts = response.data.total || 0;
      setTotalPages(Math.ceil(totalProducts / 10));
    } catch (error) {
      console.error("Error fetching products", error);
    }
  };

  const fetchCategories = async () => {
    try {
      const response = await axios.get(
        "https://dummyjson.com/products/categories"
      );
      setCategories(response.data);
    } catch (error) {
      console.error("Error fetching categories", error);
    }
  };

  const handleCategoryClick = (category) => {
    setSelectedCategory(category);
    setShowCategories(false);
    setCurrentPage(1);
  };

  const openModal = (product) => {
    setSelectedProduct(product);
  };

  const closeModal = () => {
    setSelectedProduct(null);
  };

  return (
    <div className="container">
      {/* Existing UI elements like categories dropdown, search input */}
      <div className="row">
        {products
          .filter((product) =>
            product.title.toLowerCase().includes(searchQuery.toLowerCase())
          )
          .map((product) => (
            <div key={product.id} className="col-md-4 mb-4">
              <div className="card" onClick={() => openModal(product)}>
                <div className="card-img-container">
                  <img
                    src={product.thumbnail}
                    className="card-img-top"
                    alt={product.title}
                  />
                </div>
                <div className="card-body">
                  <h5 className="card-title">{product.title}</h5>
                  <p className="card-text">${product.price}</p>
                  <button
                    className="btn btn-primary"
                    onClick={(e) => {
                      e.stopPropagation(); // Prevent modal from opening when clicking the button
                      addToCart(product);
                    }}
                  >
                    Add to Cart
                  </button>
                </div>
              </div>
            </div>
          ))}
      </div>
      {/* Modal for viewing selected product */}
      {selectedProduct && (
        <div
          className="modal"
          onClick={closeModal}
          style={{
            position: "fixed",
            top: 0,
            left: 0,
            height: "100%",
            width: "100%",
            backgroundColor: "rgba(0,0,0,0.5)",
            display: "flex",
            justifyContent: "center",
            alignItems: "center",
          }}
        >
          <div
            className="modal-content"
            onClick={(e) => e.stopPropagation()}
            style={{
              position: "relative",
              padding: "20px",
              backgroundColor: "#fff",
              borderRadius: "4px",
            }}
          >
            <span
              className="close"
              onClick={closeModal}
              style={{
                position: "absolute",
                top: "10px",
                right: "20px",
                cursor: "pointer",
                fontSize: "30px",
              }}
            >
              &times;
            </span>
            <img
              src={selectedProduct.thumbnail}
              alt={selectedProduct.title}
              style={{ maxWidth: "100%", maxHeight: "80vh" }}
            />
          </div>
        </div>
      )}

      <div className="pagination-controls">
        <button
          onClick={() => setCurrentPage(currentPage - 1)}
          disabled={currentPage === 1}
        >
          Prev
        </button>
        <span>
          Page {currentPage} of {totalPages}
        </span>
        <button
          onClick={() => setCurrentPage(currentPage + 1)}
          disabled={currentPage >= totalPages}
        >
          Next
        </button>
      </div>
    </div>
  );
}

export default HomePage;
