import React, { useState, useEffect } from "react";
import { Card, Input, Button, Select, Pagination } from "antd"; // Example with Ant Design
import "antd/dist/antd.css"; // Import Ant Design CSS

const { Meta } = Card;
const { Search } = Input;
const { Option } = Select;

function ProductPage() {
  const [products, setProducts] = useState([]);
  const [cart, setCart] = useState([]);
  const [categories, setCategories] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const [totalPages, setTotalPages] = useState(1);
  const [searchTerm, setSearchTerm] = useState("");
  const [selectedCategory, setSelectedCategory] = useState("");

  useEffect(() => {
    fetchProducts();
    fetchCategories();
  }, [currentPage, selectedCategory]);

  const fetchProducts = async () => {
    const baseUrl = "https://dummyjson.com/products";
    let url = `${baseUrl}?skip=${(currentPage - 1) * 10}&limit=10`;

    if (selectedCategory) {
      url += `&category=${selectedCategory}`;
    }

    if (searchTerm) {
      url += `&search=${searchTerm}`;
    }

    const response = await fetch(url);
    const data = await response.json();
    setProducts(data.products);
    setTotalPages(Math.ceil(data.total / 10));
  };

  const fetchCategories = async () => {
    const response = await fetch("https://dummyjson.com/products/categories");
    const data = await response.json();
    setCategories(data);
  };

  const handleAddToCart = (product) => {
    setCart([...cart, product]);
    // For actual cart functionality, you would likely have to manage cart state more robustly
  };

  const handleSearch = (value) => {
    setSearchTerm(value);
    setCurrentPage(1);
  };

  return (
    <div className="product-page">
      <Search
        placeholder="Search products"
        onSearch={handleSearch}
        enterButton
        style={{ width: 300, marginBottom: 20 }}
      />
      <Select
        defaultValue=""
        style={{ width: 200 }}
        onChange={(value) => {
          setSelectedCategory(value);
          setCurrentPage(1);
        }}
      >
        <Option value="">All Categories</Option>
        {categories.map((category) => (
          <Option key={category} value={category}>
            {category}
          </Option>
        ))}
      </Select>

      <div className="products">
        {products.map((product) => (
          <Card
            key={product.id}
            hoverable
            style={{ width: 240, margin: "16px" }}
            cover={<img alt={product.title} src={product.thumbnail} />}
            actions={[
              <Button key="add" onClick={() => handleAddToCart(product)}>
                Add to Cart
              </Button>,
            ]}
          >
            <Meta title={product.title} description={`$${product.price}`} />
          </Card>
        ))}
      </div>

      <Pagination
        current={currentPage}
        total={totalPages * 10}
        onChange={(page) => setCurrentPage(page)}
      />
    </div>
  );
}

export default ProductPage;
