import React, { useState, useEffect } from "react";
import axios from "axios";
import "../HomePage.css";

function HomePage({ addToCart }) {
  const [products, setProducts] = useState([]);
  const [categories, setCategories] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState("");
  const [showCategories, setShowCategories] = useState(false);
  const [currentPage, setCurrentPage] = useState(1);
  const [totalPages, setTotalPages] = useState(1); // Assuming API gives total count to calculate this
  const [searchTerm, setSearchTerm] = useState("");
  const [triggerFetch, setTriggerFetch] = useState(false); // Added to control fetch triggering

  // Effect to handle product fetching
  useEffect(() => {
    const fetchProducts = async () => {
      let url = "https://dummyjson.com/products";
      if (selectedCategory) {
        url += `/category/${selectedCategory}`;
      }
      url += `?skip=${(currentPage - 1) * 10}&limit=10`;

      if (searchTerm) {
        url += `&search=${searchTerm}`;
      }

      try {
        const response = await axios.get(url);
        setProducts(response.data.products || response.data);
        const totalProducts = response.data.total; // Assuming this is provided
        setTotalPages(Math.ceil(totalProducts / 10));
      } catch (error) {
        console.error("Error fetching products", error);
      }
    };

    fetchProducts();
  }, [currentPage, selectedCategory, searchTerm, triggerFetch]); // Rerun when these dependencies change

  useEffect(() => {
    const fetchCategories = async () => {
      try {
        const response = await axios.get(
          "https://dummyjson.com/products/categories"
        );
        setCategories(response.data);
      } catch (error) {
        console.error("Error fetching categories", error);
      }
    };

    fetchCategories();
  }, []);

  const handleCategoryClick = (category) => {
    setSelectedCategory(category);
    setShowCategories(false);
    setCurrentPage(1); // Reset to first page when category changes
  };

  return (
    <div className="container">
      <div className="search-bar">
        <form
          onSubmit={(e) => {
            e.preventDefault();
            setCurrentPage(1); // Ensures we go back to page 1 for a new search
            setTriggerFetch(!triggerFetch); // Toggle to trigger useEffect
          }}
        >
          <input
            type="text"
            className="form-control my-3"
            placeholder="Search by name or category..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
          />
          <button className="btn btn-primary ml-2" type="submit">
            Search
          </button>
        </form>
      </div>
      {/* Categories dropdown, products display, and pagination controls follow */}
    </div>
  );
}

export default HomePage;
