import React, { useState, useEffect } from "react";
import axios from "axios";
import "../HomePage.css";

function HomePage({ addToCart }) {
  const [products, setProducts] = useState([]);
  const [categories, setCategories] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState("");
  const [showCategories, setShowCategories] = useState(false);
  const [currentPage, setCurrentPage] = useState(1);
  const [totalPages, setTotalPages] = useState(1); // Assuming API provides enough info to calculate this
  const [searchTerm, setSearchTerm] = useState("");
  const [triggerFetch, setTriggerFetch] = useState(false); // To control fetching on search submission

  // Fetch products based on category, page, or search term changes
  useEffect(() => {
    const fetchProducts = async () => {
      let url = "https://dummyjson.com/products";
      if (selectedCategory) {
        url += `/category/${selectedCategory}`;
      }
      url += `?skip=${(currentPage - 1) * 10}&limit=10`;

      if (searchTerm) {
        url += `&search=${searchTerm}`;
      }

      try {
        const response = await axios.get(url);
        setProducts(response.data.products || response.data);
        const totalProducts = response.data.total; // Assuming this info is provided
        setTotalPages(Math.ceil(totalProducts / 10));
      } catch (error) {
        console.error("Error fetching products", error);
      }
    };

    fetchProducts();
  }, [currentPage, selectedCategory, searchTerm, triggerFetch]);

  // Fetch categories only once on component mount
  useEffect(() => {
    const fetchCategories = async () => {
      try {
        const response = await axios.get(
          "https://dummyjson.com/products/categories"
        );
        setCategories(response.data);
      } catch (error) {
        console.error("Error fetching categories", error);
      }
    };

    fetchCategories();
  }, []);

  const handleCategoryClick = (category) => {
    setSelectedCategory(category);
    setShowCategories(false);
    setCurrentPage(1); // Resets to first page for a new category
  };

  return (
    <div className="container">
      <div className="search-bar">
        <form
          onSubmit={(e) => {
            e.preventDefault();
            setCurrentPage(1); // Reset to page 1 for new search
            setTriggerFetch(!triggerFetch); // Toggle to force useEffect to run
          }}
        >
          <input
            type="text"
            className="form-control my-3"
            placeholder="Search by name or category..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
          />
          <button className="btn btn-primary ml-2" type="submit">
            Search
          </button>
        </form>
      </div>

      <div className="categories-dropdown">
        <button
          onClick={() => setShowCategories(!showCategories)}
          className="categories-toggle"
        >
          Categories
        </button>
        {showCategories && (
          <ul className="categories-menu">
            <li
              onClick={() => setSelectedCategory("")}
              className="categories-item"
            >
              All Products
            </li>
            {categories.map((category, index) => (
              <li
                key={index}
                onClick={() => handleCategoryClick(category)}
                className="categories-item"
              >
                {category}
              </li>
            ))}
          </ul>
        )}
      </div>

      <div className="row">
        {products.map((product) => (
          <div key={product.id} className="col-md-4 mb-4">
            <div className="card">
              <img
                src={product.thumbnail}
                className="card-img-top"
                alt={product.title}
              />
              <div className="card-body">
                <h5 className="card-title">{product.title}</h5>
                <p className="card-text">${product.price}</p>
                <button
                  className="btn btn-primary"
                  onClick={() => addToCart(product)}
                >
                  Add to Cart
                </button>
              </div>
            </div>
          </div>
        ))}
      </div>

      <div className="pagination-controls">
        <button
          onClick={() => setCurrentPage(currentPage - 1)}
          disabled={currentPage === 1}
        >
          Prev
        </button>
        <span>
          Page {currentPage} of {totalPages}
        </span>
        <button
          onClick={() => setCurrentPage(currentPage + 1)}
          disabled={currentPage === totalPages}
        >
          Next
        </button>
      </div>
    </div>
  );
}

export default HomePage;
