import React, { useState, useEffect } from "react";
import axios from "axios";
import "../HomePage.css";

function HomePage({ addToCart }) {
  const [products, setProducts] = useState([]);
  const [categories, setCategories] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState("");
  const [showCategories, setShowCategories] = useState(false);
  const [currentPage, setCurrentPage] = useState(1);
  const [totalPages, setTotalPages] = useState(1);
  const [searchTerm, setSearchTerm] = useState("");

  useEffect(() => {
    fetchProducts();
  }, [currentPage, selectedCategory, searchTerm]); // Now re-fetches anytime searchTerm changes too

  const fetchProducts = async () => {
    let url = "https://dummyjson.com/products";
    let params = new URLSearchParams();

    // Include pagination only if there's no searchTerm
    if (!searchTerm) {
      params.append("skip", (currentPage - 1) * 10);
      params.append("limit", 10);
    }

    if (searchTerm) {
      // Use `q` for search parameter as per the API documentation
      params.append("q", searchTerm);
    }

    if (selectedCategory && !searchTerm) {
      // Apply category only if not searching
      url += `/category/${selectedCategory}?${params.toString()}`;
    } else {
      url += `?${params.toString()}`;
    }

    console.log("Fetching from URL:", url);

    try {
      const response = await axios.get(url);
      console.log("Products fetched:", response.data.products || response.data);
      setProducts(response.data.products || []);
      if (!searchTerm) {
        const totalProducts = response.data.total || 0;
        setTotalPages(Math.ceil(totalProducts / 10));
      } else {
        // Assume single page of results for searches
        setTotalPages(1);
      }
    } catch (error) {
      console.error("Error fetching products", error);
    }
  };

  const fetchCategories = async () => {
    try {
      const response = await axios.get(
        "https://dummyjson.com/products/categories"
      );
      console.log("Categories fetched:", response.data);
      setCategories(response.data);
    } catch (error) {
      console.error("Error fetching categories", error);
    }
  };

  const handleCategoryClick = (category) => {
    setSelectedCategory(category);
    setShowCategories(false);
    setCurrentPage(1); // Reset to page 1 when category changes
  };

  return (
    <div className="container">
      <div className="search-bar">
        <form
          onSubmit={(e) => {
            e.preventDefault();
            console.log(
              "Search submitted for:",
              e.target.elements.search.value
            );
            setSearchTerm(e.target.elements.search.value);
          }}
        >
          <input
            name="search"
            type="text"
            className="form-control my-3"
            placeholder="Search by name..."
          />
          <button className="btn btn-primary ml-2" type="submit">
            Search
          </button>
        </form>
      </div>

      <div className="categories-dropdown">
        <button
          onClick={() => setShowCategories(!showCategories)}
          className="categories-toggle"
        >
          Categories
        </button>
        {showCategories && (
          <ul className="categories-menu">
            <li
              onClick={() => handleCategoryClick("")}
              className="categories-item"
            >
              All Products
            </li>
            {categories.map((category, index) => (
              <li
                key={index}
                onClick={() => handleCategoryClick(category)}
                className="categories-item"
              >
                {category}
              </li>
            ))}
          </ul>
        )}
      </div>

      <div className="row">
        {products.map((product) => (
          <div key={product.id} className="col-md-4 mb-4">
            <div className="card">
              <img
                src={product.thumbnail}
                className="card-img-top"
                alt={product.title}
              />
              <div className="card-body">
                <h5 className="card-title">{product.title}</h5>
                <p className="card-text">${product.price}</p>
                <button
                  className="btn btn-primary"
                  onClick={() => addToCart(product)}
                >
                  Add to Cart
                </button>
              </div>
            </div>
          </div>
        ))}
      </div>

      <div className="pagination-controls">
        <button
          onClick={() => setCurrentPage(currentPage - 1)}
          disabled={currentPage === 1}
        >
          Prev
        </button>
        <span>
          Page {currentPage} of {totalPages}
        </span>
        <button
          onClick={() => setCurrentPage(currentPage + 1)}
          disabled={currentPage >= totalPages}
        >
          Next
        </button>
      </div>
    </div>
  );
}

export default HomePage;
